function initializeGallery(){$.ajax({type:"POST",url:"php/getDrawingFilenames.php"}).done(function(i){imgList=JSON.parse(i).slice(0,1e3),displayNewDrawings()})}function displayNewDrawings(){loadPortion=imgList.slice(loadStart,loadStart+40),loadPortion.forEach(function(i){$(".gallery").append($("<div class='drawing'><img src=drawings/"+i+"></img></div>"))}),$("#loader-button-container").append($('<button class="loader">Load more Pokedraws!</button>')),$(".loader").on("click",function(){$(this).remove(),displayNewDrawings()}),loadStart+=40}var loadPortion=[],imgList=[],loadStart=0;$(window).scroll(function(){$(window).scrollTop()+$(window).height()>$(document).height()-500&&($(".loader").remove(),displayNewDrawings())}),initializeGallery();