function getRecentDrawings(){var n="drawings/",a=[];$.ajax({url:n,success:function(n){$(n).find("a[href*='.jpeg']").each(function(){var n=this.href.replace(window.location.host,"").replace("http:///","").replace("pokedraw/","").replace("drawings/","");a.unshift(n)})}}).done(function(){a=a.slice(0,150),displayDrawings(a)})}function displayDrawings(n){n.forEach(function(n){$(".gallery").append($("<div class='drawing'><img src=drawings/"+n+"></img></div>"))})}getRecentDrawings();